import { createStore } from 'vuex'

export default createStore({
  state: {
    loginUser: {
      memID: '',
      memPW: '',
      memName: '',
      memAddr: '',
      memPhone: '',
      memEmail: '',
      memBirth: '',
    },
  },
  getters: {
  },
  mutations: {
    updateloginUser (state, loginUserID) {
      state.loginUser.memID = loginUserID
    }
  },
  actions: {
  },
  modules: {
  }
})
