import { createStore } from 'vuex'

export default createStore({
  state: {
    loginUser: {
      memID: '',
      memPW: '',
      memName: '',
      memAddr: '',
      memPhone: '',
      memEmail: '',
      memBirth: '',
    }
  },
  getters: {
    isLogin(state) {
      return state.loginUser
    }
  },
  mutations: {
    setUser(state, loginUser) {
      state.loginUser = loginUser
    }
  },
  actions: {
  },
  modules: {
  }
})
